import React from 'react'
import Post from './components/FacebookPost'
function FacebookPost() {
    let clicked = false
    function like() {
        const element = document.getElementById('likes')
        const nLikes = element.textContent
        if(like)
            element.innerText = nLikes--
        else
            element.innerText = nLikes++
        clicked = !clicked
    }
    return (
        <div className='post'>
            <h1>Author</h1>
            <p>Description: This is a very cool post!</p>
            <button onClick={like()}></button>
            <p>Likes: <span id='likes'>0</span></p>
        </div>
    )
}

export default FacebookPost
