import React from 'react'
import ''

function Post() {
    let clicked = false
    const like = () => {
        const element = document.getElementById('likes')
        let nLikes = element.textContent
        if(clicked)
            element.innerText = parseInt(nLikes) - 1
        else
            element.innerText = parseInt(nLikes) + 1

        clicked = !clicked
    }
    return (
        <div className='post'>
            <h2>Author: Someone</h2>
            <img src='./images/postImg.jpg' alt='Post Image'></img>
            <p>Description: This is a very cool post!</p>
            <button onClick={like}>Leave a like</button>
            <p className='likeNumber'>Likes: <span id='likes'>0</span></p>
        </div>
    )
}

export default Post
