import React from 'react'
import '../style/FacebookPost.css'

function Post() {
    let clicked = false
    const data = this.props.dataFromParent
    const like = () => {
        const element = document.getElementById('likes' + data)
        let nLikes = element.textContent
        if(clicked)
            element.innerText = parseInt(nLikes) - 1
        else
            element.innerText = parseInt(nLikes) + 1

        clicked = !clicked
    }
    return (
        <div className='post'>
            <h2>Author: Someone</h2>
            <img src={require('../images/postSample.jpg')} alt='Post'></img>
            <p className='description'>The only decent image I have! Yey</p>
            <button onClick={like}>Leave a like</button>
            <p className='likeNumber'>Likes: <span id={'likes'}>0</span></p>
        </div>
    )
}

export default Post
