import React from 'react'
import FacebookPost from './FacebookPost'
import '../style/App.css'

function App() {

  const posts = []
  function getPosts() {
    for(let i = 0; i < 3; i++){
      posts.push(<FacebookPost dataFromParent = {i}/>)
    }
  }

  return (
    <div className='home'>
      {getPosts()}
      <h1>FaceBruh</h1>
      <h2>Posts:</h2>

      <div className='posts'>
        {posts.map((post, index) => (
          key={index}>{post}
        ))}
      </div>

    </div>
  )
}

export default App;
