import React, {useState} from 'react'
import '../style/FacebookPost.css'

// Let's refractor this code using the state of the function
function Post(props) {

    // let clicked = false | clicked must become part of the state of the component
    const [clicked, onClick] = useState(false)
    const [nLikes, changeAmount] = useState(0)
    const changeLikes = () =>{
        onClick(!clicked)

        if(clicked)
            changeAmount(nLikes)

    }

    // Unique identifier!
    const key = props.n
    const author = props.author
    const imgLink = props.imgLink
    const description = props.description
    const like = () => {
        const element = document.getElementById('likes - ' + key)
        let nLikes = element.textContent
        if(clicked)
            element.innerText = parseInt(nLikes) - 1
        else
            element.innerText = parseInt(nLikes) + 1

        clicked = !clicked
    }

    return (
        <div className='post'>
            <h2>Author: {author}</h2>
            <img src={imgLink} alt={author + "'s post - " + key}></img>
            <p className='description'>{description}</p>
            <button onClick={changeLikes}>Leave a like</button>
            <p className='likeNumber'>Likes: <span id={'likes - ' + key}>0</span></p>
        </div>
    )
}

export default Post
